Definition ParticipantServer__Commit: val :=
  rec: "ParticipantServer__Commit" "ps" "tid" :=
    lock.acquire (struct.loadF ParticipantServer.S "mu" "ps");;
    let: ("t", "ok") := MapGet (struct.loadF ParticipantServer.S "txns" "ps") "tid" in
